import React from "react";

class GoogleOauth extends React.Component {
  state = {
    isSignin: null,
  };

  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "354023458035-kuuevr619sk7o4tlhhcv9ofkfl0640cj.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({ isSignin: this.auth.isSignedIn.get() });
          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }
  onAuthChange = () => {
    this.setState({
      isSignin: this.auth.isSignedIn.get(),
    });
  };
  onSignIn = () => {
    this.auth.signIn();
  };
  onSignOut = () => {
    this.auth.signOut();
  };
  renderSignedIn() {
    if (this.state.isSignin === null) {
      return <p>I dont know</p>;
    } else if (this.state.isSignin) {
      return <button onClick={this.onSignOut}>SignOut</button>;
    } else {
      return <button onClick={this.onSignIn}>SignIn</button>;
    }
  }
  render() {
    console.log("sdfgm", this.state.isSignin);
    return <h1> {this.renderSignedIn()}</h1>;
  }
}
export default GoogleOauth;
