import strems from "../apis/strems";
export const signIn = (userId) => {
  return {
    type: "SIGN_IN",
    payload: userId,
  };
};

export const signOut = () => {
  return {
    type: "SIGN_OUT",
  };
};

export const createStrems = (formValue) => async (dispatch) => {
  const response = await strems.post("/streams", formValue);
  dispatch({ type: "CREATE_STRME", payload: response.data });
};
