import React from "react";
import { div } from "react-bootstrap";
import { connect } from "react-redux";
import { getallStrmes } from "../actions/index";
class ListStream extends React.Component {
  componentDidMount() {
    this.props.getallStrmes();
  }

  // rendeList() {
  //   // console.log("sdfgn", this.props.strems);
  //   this.props.strems.map((item) => {
  //     console.log("item", item.title);
  //     return (
  //       <>
  //         <Card>
  //           <h2>{item.title}</h2>
  //           <p>{item.discription}</p>
  //         </Card>
  //       </>
  //     );
  //   });
  // }

  renderAdmin(stream) {
    if (stream.userId === this.props.currentUserID) {
      return (
        <>
          <button>Edit</button>
          <button>Delete</button>
        </>
      );
    }
  }

  render() {
    return (
      <div>
        <h1>Streams</h1>
        {this.props.strems.map((item) => {
          return (
            <>
              <div
                style={{
                  borderTop: "1px solid",
                  borderBottom: "1px solid",
                  display: "flex",
                  justifyContent: "space-between",
                }}
              >
                <div>
                  <h2>{item.title}</h2>
                  <p>{item.discription}</p>
                </div>

                <div>{this.renderAdmin()}</div>
              </div>
            </>
          );
        })}
      </div>
    );
  }
}
const mapstatetoProps = (state) => {
  return {
    strems: Object.values(state.strmes),
    currentUserID: state.auth.userId,
  };
};
export default connect(mapstatetoProps, { getallStrmes })(ListStream);
