import strems from "../apis/strems";
export const signIn = (userId) => {
  return {
    type: "SIGN_IN",
    payload: userId,
  };
};

export const signOut = () => {
  return {
    type: "SIGN_OUT",
  };
};

export const createStrems = (formValue) => (dispatch) => {
  strems.post("/strems", formValue);
};
