import React from "react";
import { div } from "react-bootstrap";
import { connect } from "react-redux";
import { getallStrmes } from "../actions/index";
class ListStream extends React.Component {
  componentDidMount() {
    this.props.getallStrmes();
  }

  // rendeList() {
  //   // console.log("sdfgn", this.props.strems);
  //   this.props.strems.map((item) => {
  //     console.log("item", item.title);
  //     return (
  //       <>
  //         <Card>
  //           <h2>{item.title}</h2>
  //           <p>{item.discription}</p>
  //         </Card>
  //       </>
  //     );
  //   });
  // }

  render() {
    return (
      <div>
        <h1>Streams</h1>
        {this.props.strems.map((item) => {
          return (
            <>
              <div
                style={{
                  borderTop: "1px solid",
                  borderBottom: "1px solid",
                  display: "flex",
                  justifyContent: "space-between",
                }}
              >
                <div>
                  <h2>{item.title}</h2>
                  <p>{item.discription}</p>
                </div>
                <div>
                  <button>Edit</button>
                  <button>Delete</button>
                </div>
              </div>
            </>
          );
        })}
      </div>
    );
  }
}
const mapstatetoProps = (state) => {
  return { strems: Object.values(state.strmes) };
};
export default connect(mapstatetoProps, { getallStrmes })(ListStream);
