import React from "react";
import { connect } from "react-redux";
import { FetchPostAndUser } from "../actions";
import image from "./logo192.png";
import Userheader from "./userHeader";
class PostList extends React.Component {
  componentDidMount() {
    this.props.FetchPostAndUser();
  }

  render() {
    return this.props.posts.map((item) => {
      return (
        <>
          <div style={{ width: "720px", margin: "auto" }}>
            <div style={{ display: "flex" }}>
              <div>
                <img src={image} height="50px" alt="sdfg" />
              </div>

              <div>
                {" "}
                <h1>{item.title}</h1>
                <p>{item.body}</p>
              </div>
            </div>
            <Userheader id={item.userId} />
            <hr />
          </div>
        </>
      );
    });
  }
}
const mapStetoProps = (state) => {
  return {
    posts: state.posts,
  };
};
export default connect(mapStetoProps, { FetchPostAndUser })(PostList);
