import jsonplaceholder from "../apis/jsonplaceholder";
import _ from "lodash";

export const FetchPostAndUser = () => async (dispatch, getState) => {
  await dispatch(FetchPost());
  const UserIDs = _.map((getState().posts, "userId"));
  UserIDs.forEach((item) => dispatch(FetchUsers(item)));
};

export const FetchPost = () => async (dispatch, getState) => {
  const response = await jsonplaceholder.get("/posts");
  dispatch({ type: "FETCH_POSTS", payload: response.data });
};

export const FetchUsers = (id) => async (dispatch, getState) => {
  const response = await jsonplaceholder.get(`/users/${id}`);
  dispatch({ type: "FETCH_USERS", payload: response.data });
};

